mapper.update:
update t_question
set content = #{content,jdbcType=VARCHAR},
  `type` = #{type,jdbcType=VARCHAR},
  `level` = #{level,jdbcType=VARCHAR},
  createTime = #{createtime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select * from t_question q 

	1=1
	 AND q.id = #{id,jdbcType=INTEGER}
	 AND q.content = #{content,jdbcType=VARCHAR}
	 AND q.type = #{type,jdbcType=VARCHAR}
	 AND q.level = #{level,jdbcType=VARCHAR}
	 AND q.createtime = #{createtime,jdbcType=TIMESTAMP}
